import React from "react";
import CollectionItem from "../../components/collections-item/collection-item.component";
import { connect } from "react-redux";
import { selectCollection } from "../../redux/shop/shop.selector";
import "./collection.component.style.scss";
const CollectionPage = ({ collection }) => {
  const { hats } = collection;
  return (
    <div className="collection-page">
      <h2 className="title">{hats}</h2>
      <div className="items">
        {hats.map((item) => (
          <CollectionItem key={item.id} item={hats} />
        ))}
      </div>
    </div>
  );
};
const mapStateToProps = (state, ownProps) => ({
  collection: selectCollection(ownProps.match.params.CollecitonId)(state), // here is currying, first we need arg1 and pass into arg2(state)
});
export default connect(mapStateToProps)(CollectionPage);
