import { createSelector } from "reselect";
const selectUser = (state) => state.user; //(rootreducer == state) => currentUser: state.user.currentUser
export const selectCurrentUser = createSelector(
  selectUser, //store state in here,  state.user.currentUser
  (user) => user.currentUser  //
);
