import React from "react";
import { loadStripe } from "@stripe/stripe-js";
// Make sure to call `loadStripe` outside of a component’s render to avoid
// recreating the `Stripe` object on every render.
const stripePromise = loadStripe(
  "pk_test_51Ix4EGGRC00fpJdOUbItU1G6TpFaEeL1TIkgTyM03XGjrWzmm8Oe1O2haFg3pq6LbJKG8chVR7llGwFcgZEQjDlG003cLwL90U"
);

const StripeCheckoutButton = ({ price, cartItems }) => {
  const StripeCheckout = async (event) => {
    // When the customer clicks on the button, redirect them to Checkout.
    const stripe = await stripePromise;
    const { quantity } = cartItems;
    const { error } = await stripe.redirectToCheckout({
      lineItems: [
        {
          price: { price }, // Replace with the ID of your price
          quantity: { quantity },
        },
      ],
      mode: "payment",
      successUrl: `http://localhost:3000`,
    });

    // If `redirectToCheckout` fails due to a browser or network
    // error, display the localized error message to your customer
    // using `error.message`.
  };
  return <button onClick={StripeCheckout}>Check out</button>;
};

export default StripeCheckoutButton;

